INSERT INTO t_POST (ID_POST, NAME, TAGS, TEXT, COMMENT_COUNT, VIZIT_COUNT, IS_MAIN, IS_HIDDEN, VOTING_PLUS, VOTING_MINUS, ID_USER_MODIFY, DATE_MODIFY, ID_USER_CREATE, DATE_CREATE) VALUES (57, 'debian7 ÐºÐ°Ðº Ð´Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÑÑ‚Ð°Ð½Ð´Ð°Ñ€Ñ‚Ð½Ñ‹Ðµ Ñ€ÐµÐ¿Ð¾Ð·Ð¸Ñ‚Ð¾Ñ€Ð¸Ð¸  ', 'nix linux debian apt-get Ð´Ð¾Ð±Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ñ€ÐµÐ¿Ð¾Ð·Ð¸Ñ‚Ð¾Ñ€Ð¸Ð¸ add repositories  ', 'Ð´Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ ÑÐ»ÐµÐ´ÑƒÑŽÑ‰Ð¸Ðµ Ñ€ÐµÐ¿Ð¾Ð·Ð¸Ñ‚Ð¾Ñ€Ð¸Ð¸ Ð² Ñ„Ð°Ð¹Ð»  /etc/apt/sources.list  <code>  echo deb http://ftp.ru.debian.org/debian/ wheezy main >>  /etc/apt/sources.list  echo deb-src http://ftp.ru.debian.org/debian/ wheezy main >> /etc/apt/sources.list    echo deb http://security.debian.org/ wheezy/updates main >>  /etc/apt/sources.list  echo deb-src http://security.debian.org/ wheezy/updates main >>  /etc/apt/sources.list    echo deb http://ftp.ru.debian.org/debian/ wheezy-updates main >>  /etc/apt/sources.list  echo deb-src http://ftp.ru.debian.org/debian/ wheezy-updates main >>  /etc/apt/sources.list    apt-get update  </code>    ', 0, 0, 0, 0, NULL, NULL, NULL, '2015-04-16 10:52:42', NULL, '2014-10-13 11:47:21');
INSERT INTO t_POST (ID_POST, NAME, TAGS, TEXT, COMMENT_COUNT, VIZIT_COUNT, IS_MAIN, IS_HIDDEN, VOTING_PLUS, VOTING_MINUS, ID_USER_MODIFY, DATE_MODIFY, ID_USER_CREATE, DATE_CREATE) VALUES (58, 'linux ÑƒÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° lamp (apache,php,mysql)    ', '', '<code>  apt-get install apache2  apt-get install mysql-server   mysql_install_db  mysql_secure_installation  apt-get install php5 libapache2-mod-php5 php5-mcrypt php5-mysql  </code>    Ð´Ð°Ð»ÐµÐµ Ð¿Ñ€Ð°Ð²Ð¸Ð¼ ÑÐ¾Ð´ÐµÑ€Ð¶Ð¸Ð¼Ð¾Ðµ Ñ„Ð°Ð¹Ð»Ð° /etc/apache2/mods-enabled/dir.conf  #nano /etc/apache2/mods-enabled/dir.conf   Ðº Ñ‚Ð°ÐºÐ¾Ð¼Ñƒ Ð²Ð¸Ð´Ñƒ:   <code>  &lt;IfModule mod_dir.c>      DirectoryIndex index.php index.html index.cgi index.pl index.xhtml index.htm  &lt;/IfModule>  </code>     Ð´Ð°Ð»ÐµÐµ  service apache2 restart   ', 0, 0, 0, 0, NULL, NULL, NULL, '2014-10-13 18:39:11', NULL, '2014-10-13 12:06:01');
