INSERT INTO t_POST (ID_POST, NAME, TAGS, TEXT, COMMENT_COUNT, VIZIT_COUNT, IS_MAIN, IS_HIDDEN, VOTING_PLUS, VOTING_MINUS, ID_USER_MODIFY, DATE_MODIFY, ID_USER_CREATE, DATE_CREATE) VALUES (61, 'linux ÐºÐ°Ðº Ð¿ÐµÑ€ÐµÐ½ÐµÑÑ‚Ð¸ ÑÐ°Ð¹Ñ‚Ñ‹ Ñ Ð¾Ð´Ð½Ð¾Ð³Ð¾ Ñ…Ð¾ÑÑ‚Ð¸Ð½Ð³Ð° Ð½Ð° Ð´Ñ€ÑƒÐ³Ð¾Ð¹      ', 'linux, apache php mysql lamp config ÑƒÑÑ‚Ð°Ð½Ð¾Ð²ÐºÐ° Ð½Ð°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ° Ð¿ÐµÑ€ÐµÐ½Ð¾Ñ     ', '<code>  #ÐºÐ¾Ð¿Ð¸Ñ€ÑƒÐµÐ¼ Ñ„Ð°Ð¹Ð»Ñ‹ Ð½Ð° Ð½Ð¾Ð²Ñ‹Ð¹ ÑÐµÑ€Ð²ÐµÑ€:  scp path/myfile user@8.8.8.8:/full/path/to/new/location/     #Ð¸Ð»Ð¸ Ñ‚Ð°Ðº:  #ÑÐºÐ°Ñ‡Ð°Ñ‚ÑŒ  curl scp://login:password@hostname/path/to/file -o /path/to/local/out/file  #Ð·Ð°Ð³Ñ€ÑƒÐ·Ð¸Ñ‚ÑŒ  curl -T upload_this_file scp://login:password@hostname/path/to/file      #ÑÑ‚Ð°Ð²Ð¸Ð¼ Ð¸ Ð½Ð°ÑÑ‚Ñ€Ð°Ð¸Ð²Ð°ÐµÐ¼ mysql:  apt-get install mysql-server  mysql_install_db  mysql_secure_installation    #Ð±Ð°Ð·Ñ‹ mysql Ð¾Ð±Ñ‹Ñ‡Ð½Ð¾ Ñ‚ÑƒÑ‚:  cd /usr/share/mysql/    #Ð¿Ð¾ÑÐ»Ðµ Ñ‚Ð¾Ð³Ð¾ ÐºÐ°Ðº Ð±Ð°Ð·Ñ‹ Ð·Ð°ÐºÐ¸Ð½ÑƒÐ»Ð¸, Ð¿Ñ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð½Ð¾Ñ€Ð¼Ð°Ð»ÑŒÐ½Ð¾ Ð»Ð¸ ÑÑ‚Ð°Ñ€Ñ‚ÑƒÐµÑ‚ mysql  service mysql restart    #Ð² ÑÐ»ÑƒÑ‡Ð°Ðµ Ð¾ÑˆÐ¸Ð±Ð¾Ðº Ð²Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÐ¼  mysqlcheck --check-upgrade --all-databases --auto-repair -u root -p  mysql_upgrade --force -u root -p    #Ð¸Ð»Ð¸:  mysqlcheck --check-upgrade --all-databases --auto-repair -u root --password=123456  mysql_upgrade --force -u root --password=123456      #ÑƒÐ´Ð°Ð»ÑÐµÐ¼ Ð°Ð¿Ð°Ñ‡ ÑÐ¾ ÑÑ‚Ð°Ñ€Ñ‹Ð¼Ð¸ ÐºÐ¾Ð½Ñ„Ð¸Ð³Ð°Ð¼Ð¸  apt-get --purge remove apache2 apache2.2-common apache2-utils  apt-get autoremove    #Ð¿Ð¾Ñ‚Ð¾Ð¼ ÑÑ‚Ð°Ð²Ð¸Ð¼ Ð¿Ð¾ Ð½Ð¾Ð²Ð¾Ð¹ Ð²Ð¼ÐµÑÑ‚Ðµ Ñ Ð¿Ñ…Ð¿:  apt-get install apache2 php5 libapache2-mod-php5 php5-mcrypt php5-mysql    #Ð¿Ñ€Ð°Ð²Ð¸Ð¼ ÐºÐ¾Ð½Ñ„Ð¸Ð³Ð¸ Ð´Ð»Ñ ÑÐ°Ð¹Ñ‚Ð¾Ð² Ñ‚ÑƒÑ‚:  /etc/apache2/sites-available/default    <code>  #Ð½Ð°Ð¿Ñ€Ð¸Ð¼ÐµÑ€ Ð´Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ ÑÐ»ÐµÐ´. ÑÑ‚Ñ€Ð¾ÐºÐ¸ Ð´Ð»Ñ ÐºÐ°Ð¶Ð´Ð¾Ð³Ð¾ ÑÐ°Ð¹Ñ‚Ð°:  <VirtualHost *:80>    ServerName xn----8sbkrwdc1bl.xn--p1ai    ServerAlias fond-inta.ru www.fond-inta.ru www.xn----8sbkrwdc1bl.xn--p1ai    DocumentRoot "/mnt/sites/fond-inta.ru/www"   <Directory "/mnt/sites/fond-inta.ru/www">       Options Indexes FollowSymLinks       AllowOverride none       Order allow,deny              Allow from all   </Directory>  </VirtualHost>  </code>  </code>      ', 0, 0, 0, 0, NULL, NULL, NULL, '2014-11-21 17:18:27', NULL, '2014-10-14 10:41:35');
