INSERT INTO t_POST (ID_POST, NAME, TAGS, TEXT, COMMENT_COUNT, VIZIT_COUNT, IS_MAIN, IS_HIDDEN, VOTING_PLUS, VOTING_MINUS, ID_USER_MODIFY, DATE_MODIFY, ID_USER_CREATE, DATE_CREATE) VALUES (63, 'git Ð¸ Ð¿ÑƒÑ‚Ð¸ Ðº Ñ„Ð°Ð¹Ð»Ð°Ð¼ Ð² Ð½Ð¾Ñ€Ð¼Ð°Ð»ÑŒÐ½Ð¾Ð¹ ÐºÐ¾Ð´Ð¸Ñ€Ð¾Ð²ÐºÐµ   ', 'git Ð³Ð¸Ñ‚ log Ð»Ð¾Ð³ ÐºÐ¾Ð´Ð¸Ñ€Ð¾Ð²ÐºÐ° ÑÐ¿ÐµÑ† ÑÐ¸Ð¼Ð²Ð¾Ð»Ñ‹ Ð¿Ð°Ð¿ÐºÐ¸ Ñ†Ð¸Ñ„Ñ€Ð¾Ð²Ñ‹Ðµ encoding quotepath config Ñ€ÑƒÑÑÐºÐ¸Ðµ Ð¸Ð¼ÐµÐ½Ð° Ñ„Ð°Ð¹Ð»Ð¾Ð² ', 'ÐµÑÐ»Ð¸ Ð²Ð¼ÐµÑÑ‚Ð¾ Ñ€ÑƒÑÑÐºÐ¸Ñ… ÑÐ¸Ð¼Ð²Ð¾Ð»Ð¾Ð² Ð² Ð»Ð¾Ð³Ðµ Ð³Ð¸Ñ‚Ð° Ð¼Ñ‹ Ð²Ð¸Ð´Ð¸Ð¼ Ñ‡Ñ‚Ð¾ Ñ‚Ð¾ Ð½ÐµÐ¿Ð¾Ð½ÑÑ‚Ð½Ð¾Ðµ, Ð¿Ð¾Ñ…Ð¾Ð¶ÐµÐµ Ð²Ð¾Ñ‚ Ð½Ð° ÑÑ‚Ð¾:<code>   create mode 100755 "hehe/\320\276\321\202\320\272/\320\2441/100_6839.JPG"  </code>    Ñ‚Ð¾ ÑÑ‚Ð¾ Ð»ÐµÐ³ÐºÐ¾ Ð¸ÑÐ¿Ñ€Ð°Ð²Ð»ÑÐµÑ‚ÑÑ Ð½ÐµÑÐºÐ¾Ð»ÑŒÐºÐ¸Ð¼Ð¸ ÐºÐ¾Ð¼Ð°Ð½Ð´Ð°Ð¼Ð¸:<code>  git config --global --unset i18n.logoutputencoding  git config --global --unset i18n.commitencoding  git config --global core.quotepath off  </code>    Ð¸Ð»Ð¸ Ð²Ñ€ÑƒÑ‡Ð½ÑƒÑŽ Ð¿Ð¾Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ñ„Ð°Ð¹Ð» ÐºÐ¾Ð½Ñ„Ð¸Ð³Ð° .gitconfig, Ð´Ð¾Ð±Ð°Ð²Ð¸Ð² Ñ‚ÑƒÐ´Ð° ÑÐ»ÐµÐ´ÑƒÑŽÑ‰ÑƒÑŽ ÑÑ‚Ñ€Ð¾ÐºÑƒ Ð² Ñ€Ð°Ð·Ð´ÐµÐ» [core]:<code>      [core]      quotepath = off  </code>   ', 0, 0, 0, 0, NULL, NULL, NULL, '2014-10-17 16:10:51', NULL, '2014-10-17 16:09:22');
