INSERT INTO t_POST (ID_POST, NAME, TAGS, TEXT, COMMENT_COUNT, VIZIT_COUNT, IS_MAIN, IS_HIDDEN, VOTING_PLUS, VOTING_MINUS, ID_USER_MODIFY, DATE_MODIFY, ID_USER_CREATE, DATE_CREATE) VALUES (81, 'ÑÐºÑ€Ð¸Ð¿Ñ‚ Ð´Ð»Ñ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ð±ÑÐºÐ°Ð¿Ð° Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ ÐµÐ³Ð¾ Ñ win Ñ…Ð¾ÑÑ‚Ð° Ð½Ð° linux ', 'backup script Ð±ÑÐºÐ°Ð¿Ñ‹ Ð±ÐµÐºÐ°Ð¿Ñ‹ ÑÐºÑ€Ð¸Ð¿Ñ‚ Ð±Ð°Ñ‚Ð½Ð¸Ðº cmd bat psftp pscp   ', '<code>  @echo off  :: ÑÐºÑ€Ð¸Ð¿Ñ‚ Ð´Ð»Ñ ÑÐ¾Ð·Ð´Ð°Ð½Ð¸Ñ Ð±ÑÐºÐ°Ð¿Ð¾Ð²  GOTO set_my_date_time  :my_date_time_is_set    ::ECHO %~dp0 - Ñ‚ÐµÐºÑƒÑ‰Ð¸Ð¹ Ð¿ÑƒÑ‚ÑŒ Ðº Ð±Ð°Ñ‚Ð½Ð¸ÐºÑƒ    SET   backupFrom="d:\disk_x__1c"  SET     backupTo=d:\backup\1c_bases\%yy%-%mo%  SET    backupTo2=e:\backup\1c_bases\%yy%-%mo%  SET   backupName=1c_bases__%my_date_time%      SET PATH=c:\Program Files\7-Zip;c:\Program Files (x86)\7-Zip;%PATH%  SET PATH=d:\Program Files\7-Zip;d:\Program Files (x86)\7-Zip;%PATH%  SET PATH=e:\Program Files\7-Zip;e:\Program Files (x86)\7-Zip;%PATH%  SET PATH=f:\Program Files\7-Zip;f:\Program Files (x86)\7-Zip;%PATH%  SET PATH=f:\port_programs\archiv\7-Zip;f:\pp\archiv\7-Zip;%PATH%       SET PATH=e:\port_programs\archiv\7-Zip;e:\pp\archiv\7-Zip;%PATH%       SET PATH=c:\port_programs\archiv\7-Zip;c:\pp\archiv\7-Zip;%PATH%       SET PATH=d:\port_programs\archiv\7-Zip;d:\pp\archiv\7-Zip;%PATH%           @echo ====================================================  @7z.exe a -t7z "%backupTo%/%backupName%.7z" %backupFrom% -m"x=9" -i@"%~dp0make_backup__include_files.txt"  ::-x@"%~dp0make_backup__exclude_files.txt"   ::-i@"%~dp0make_backup__include_files.txt"    @copy "%backupTo%/%backupName%.7z" "%backupTo2%/%backupName%.7z"    @echo ====================================================  @echo backup:  @echo %backupTo%/%backupName%.7z    @echo %date% %time% START SEND FILE: "%backupTo%/%backupName%.7z" >> %~dp0send_log.txt  ::Ñ‚.Ðº. psftp Ð¾ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÑ‚ Ñ€Ð°Ð±Ð¾Ñ‚Ñƒ Ð² ÑÐ»ÑƒÑ‡Ð°Ðµ Ð¾ÑˆÐ¸Ð±Ð¾Ðº (Ð½Ð°Ð¿Ñ€Ð¸Ð¼ÐµÑ€ ÐµÑÐ»Ð¸ Ð¿Ñ‹Ñ‚Ð°ÐµÐ¼ÑÑ ÑÐ¾Ð·Ð´Ð°Ñ‚ÑŒ ÑƒÐ¶Ðµ ÑÑƒÑ‰ÐµÑÑ‚Ð²ÑƒÑŽÑ‰Ð¸Ð¹ ÐºÐ°Ñ‚Ð°Ð»Ð¾Ð³)  ::Ð¼Ñ‹ Ð²Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÐ¼ ÐµÐ³Ð¾ Ð² 2 Ð·Ð°Ñ…Ð¾Ð´Ð°: 1 - ÑÐ¾Ð·Ð´Ð°ÐµÐ¼ ÐºÐ°Ñ‚Ð°Ð»Ð¾Ð³, 2 - Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐµÐ¼ Ð² Ð½ÐµÐ³Ð¾ Ñ„Ð°Ð¹Ð»  @echo mkdir /backups/1c__192.168.1.10/%yy%-%mo% > %~dp0script1.scr  @echo exit >> %~dp0script1.scr  %~dp0psftp.exe bakuser@192.168.1.11 -pw 123456 -b script1.scr >> %~dp0send_log.txt 2>&1    @echo cd /backups/1c__192.168.1.10/%yy%-%mo% > %~dp0script2.scr  @echo put "%backupTo%/%backupName%.7z" >> %~dp0script2.scr  @echo exit >> %~dp0script2.scr  %~dp0psftp.exe bakuser@192.168.1.11 -pw 123456 -b script2.scr >> %~dp0send_log.txt 2>&1  @echo %date% %time%   END SEND FILE: "%backupTo%/%backupName%.7z" >> %~dp0send_log.txt  ::%~dp0pscp -v "%backupTo%/%backupName%.7z" bakuser@192.168.1.11:/backups/1c__192.168.1.10  >> log_%yy%-%mo%.txt      if "%1"=="no_pause" GOTO end  ::@pause  :end        GOTO end_set_my_date_time  :set_my_date_time  @echo off  ::ÑÐºÑ€Ð¸Ð¿Ñ‚ Ð¸ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐµÑ‚ date.exe Ð¾Ñ‚ UnixUtils  (sourceforge.net/projects/unxutils/files) Ð¿ÐµÑ€ÐµÐ¸Ð¼ÐµÐ½Ð¾Ð²Ð°Ð½Ð½Ñ‹Ð¹ Ð² make_backup__date.exe  for /f "tokens=1,2,3,4,5,6* delims=," %%i in (''%~dp0make_backup__date.exe +"%%Y,%%m,%%d,%%H,%%M,%%S"'') do set yy=%%i& set mo=%%j& set dd=%%k& set hh=%%l& set mm=%%m& set ss=%%n  echo %yy% %mo% %dd% %hh% %mm% %ss%  SET my_date=%yy%-%mo%-%dd%  SET my_time=%hh%-%mm%-%ss%  SET my_date_time=%my_date%__%my_time%  GOTO my_date_time_is_set  :end_set_my_date_time  </code>      ', 0, 0, 0, 0, NULL, NULL, NULL, '2015-04-24 16:54:09', NULL, '2015-04-24 09:53:20');
